import React from "react";
import { useHistory } from "react-router-dom";
import "./login.css";

const Login = () => {
  let history = useHistory();

  const validateForm = (e) => {
    e.preventDefault();
    var firstName = document.getElementById("first_name").value;
    var password = document.getElementById("password").value;
    let data = {
      firstName: firstName,
      password: password,
    };
    checkLogin(data);
  };

  const checkLogin = (data) => {
    const { firstName, password } = data;
    if (firstName === "Admin" && password === "Admin@123") {
      alert("Admin Login Success");
      history.push("/employees");
    } else {
      alert("Manager Login Success");
      history.push("/employee2");
    }
    
  };


  return (
    <div className="login-container con">
      <div className="formManage">
        <h2 className="text-success text-center">Login Form</h2>
        <form onSubmit={validateForm}>
          <div className="form-group">
            <label htmlFor="first_name">First Name:</label>
            <input
              type="text"
              id="first_name"
              name="first_name"
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <input
              type="submit"
              value="Login"
              className="btn btn-primary mt-2 btnsub"
            />
          </div>
        </form>
        <div className="register-link mt-2 reg">
          New User? <a href="http://localhost:3000/register">Register</a>
        </div>
      </div>
    </div>
  );
};

export default Login;
