import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import "./login.css";

const Login = () => {
  let history = useHistory();
  const [error, setError] = useState("");

  const validateForm = async (e) => {
    e.preventDefault();
    const firstName = document.getElementById("first_name").value;
    const password = document.getElementById("password").value;

    try {
      const response = await fetch("http://localhost:8080/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ firstName, password }),
      });

      if (response.status === 200) {
        const user = await response.json();
        if (user.firstName === "Admin") {
          alert("Login Success");
          history.push("/employees");
        } else {
          alert("Login Success (Employee)");
          history.push("/employees2");
        }
      } else {
        setError("Failed to login");
      }
    } catch (error) {
      console.log(error);
      setError("Failed to login");
    }
  };

  return (
    <div className="login-container con">
      <div className="formManage">
        <h2 className="text-success text-center">Login Form</h2>
        <form onSubmit={validateForm}>
          <div className="form-group">
            <label htmlFor="first_name">First Name:</label>
            <input
              type="text"
              id="first_name"
              name="first_name"
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <input
              type="submit"
              value="Login"
              className="btn btn-primary mt-2 btnsub"
            />
          </div>
        </form>
        <div className="register-link mt-2 reg">
          New User? <a href="http://localhost:3000/register">Register</a>
        </div>
        {error && <div className="error-message">{error}</div>}
      </div>
    </div>
  );
};

export default Login;
